#!/usr/bin/env bash

set -euo pipefail



# -------- 配置 --------

COUCH=&quot;http://admin:123456@localhost:5984&quot;

DB=&quot;obsidiannotes&quot;
LAST_SEQ_FILE=&quot;$HOME/quartz/.couch_last_seq&quot;

CONTENT_DIR=&quot;$HOME/quartz/content&quot;

QUARTZ_DIR=&quot;$HOME/quartz&quot;

PAGES_DIR=&quot;$HOME/quartz-pages&quot;

# ----------------------



log() { echo &quot;$(date &#039;+%F %T&#039;) $*&quot;; }



# 首次序列号

last_seq=$(curl -s &quot;$COUCH/$DB/_changes?descending=true&amp;limit=1&quot; | jq -r &#039;.last_seq&#039;)

[[ -f &quot;$LAST_SEQ_FILE&quot; ]] &amp;&amp; last_seq=$(cat &quot;$LAST_SEQ_FILE&quot;)



while true; do

log &quot;Waiting for changes since $last_seq ...&quot;

res=$(curl -sN &quot;$COUCH/$DB/_changes?feed=longpoll&amp;timeout=25000&amp;since=$last_seq&amp;include_docs=false&quot;)

new_seq=$(echo &quot;$res&quot; | jq -r &#039;.last_seq&#039;)

[[ &quot;$new_seq&quot; == &quot;$last_seq&quot; ]] &amp;&amp; continue



log &quot;Detected change, syncing...&quot;



# ---------- 1. 导出 ----------

log &quot;Exporting notes...&quot;

find &quot;$CONTENT_DIR&quot; -type f -name &#039;*.md&#039; ! -name &#039;index.md&#039; ! -name &#039;.gitkeep&#039; -delete



total=$(curl -s &quot;$COUCH/$DB&quot; | jq &#039;.doc_count&#039;)



for i in $(seq 0 $((total-1))); do

doc=$(curl -s &quot;$COUCH/$DB/_all_docs?limit=1&amp;skip=$i&amp;include_docs=true&quot; | jq -r &#039;.rows[0].doc // empty&#039;)

[[ -z $doc ]] &amp;&amp; continue



type=$(echo &quot;$doc&quot; | jq -r &#039;.type // &quot;&quot;&#039;)

[[ &quot;$type&quot; != &quot;plain&quot; &amp;&amp; &quot;$type&quot; != &quot;newnote&quot; ]] &amp;&amp; continue



id=$(echo &quot;$doc&quot; | jq -r &#039;._id&#039;)

fname=$(echo &quot;$doc&quot; | jq -r &#039;.path // ._id&#039; | tr -d &#039;\r&#039;)

fname=${fname%.md}



content=&quot;&quot;

for cid in $(echo &quot;$doc&quot; | jq -r &#039;.children[] // empty&#039;); do

chunk=$(curl -s &quot;$COUCH/$DB/$cid&quot;)
data=$(echo &quot;$chunk&quot; | jq -r &#039;.data // &quot;&quot;&#039;)

content+=&quot;$data&quot;

done



title=$(echo &quot;$doc&quot; | jq -r &#039;.title // empty&#039;)

[[ -z $title ]] &amp;&amp; title=&quot;$fname&quot;



cat &gt; &quot;$CONTENT_DIR/$fname.md&quot; &lt;&lt;EOF

---

title: $title

---



$content

EOF

done

# ---------- 2. 构建 ----------

log &quot;Building Quartz...&quot;

cd &quot;$QUARTZ_DIR&quot;

npx quartz build



# ---------- 3. 部署 ----------

log &quot;Deploying to Pages...&quot;

rsync -a --delete --exclude=&#039;.git*&#039; &quot;$QUARTZ_DIR/public/&quot; &quot;$PAGES_DIR/&quot;

cd &quot;$PAGES_DIR&quot;

git add .

git diff --cached --quiet || {

git commit -m &quot;auto build $(date -Iseconds)&quot;

git push origin main

}



echo &quot;$new_seq&quot; &gt; &quot;$LAST_SEQ_FILE&quot;

last_seq=$new_seq

log &quot;Sync complete.&quot;

done
